import React, { useState, useEffect } from "react";
import axios from "axios";
import { Heading, GridItem, Grid, Image, Text,ChakraProvider, SimpleGrid, Container } from "@chakra-ui/react";
function Recipes_api() {
  const [recipe, setRecipe] = useState([]);

  useEffect(() => {
    const loadPost = async () => {
      const response = await axios.get(
        "http://95.111.202.157:8001/api/v1/getAllRecipes"
      );  
      console.log("ajay", response); 

      setRecipe(response.data);
    };

    loadPost();
  }, []);
  const dataList = [
    {
      id: "1",
      product: "Product 1",
      summary: "This is a summary, can be any length",
      longLine: "Very short, can be any description"
    },
    {
      id: "2",
      product: "Product Two",
      summary:
        "Another summary, make sure that this is very responsivesfafsdfsdfsdfsdfsfsfsdf",
      longLine: "Billy Bob Bob Bob Bob likes Markiplier gameplay videos"
    },
    {
      id: "3",
      product: "Long Product",
      summary: "Finalize them summary, hurry, we are close to deadline",
      longLine: "Wow, this is very descriptive! I wonder how long it is"
    }
  ];

  return (
    <ChakraProvider>
      <Container maxW="80rem" centerContent>
        <SimpleGrid columns={[1, 2, 1, 2]}>
          {dataList.map(function (data) {
            const { id, product, summary, longLine } = data;
            return (
              <Card
                key={id}
                product={product}
                summary={summary}
                longLine={longLine}
              />
            );
          })}
        </SimpleGrid>
      </Container>
    </ChakraProvider>
  );


  return (
    <>
      <div>
        <Text align="center" marginTop="100px">
          <Heading>Simple and tasty recipes</Heading>
          <Text>Lorem ipsum dolor sit amet consectetur ipsum dolor sit amet consectetur </Text>
          <Text>Lorem ipsum dolor sit amet consectetur, </Text>
        </Text>
        <Grid templateColumns="repeat(6, 1fr)" gap={6} m={10}>
          {recipe.map((image) => (
            <GridItem w="100%" h="10" key={image._id} m={10}>
              <Image
                w="100px"
                src={"http://95.111.202.157:8001/" + image.recipeId.image}
              />
              <Text width="250px" height="6px">
                {image.recipeId.description}
              </Text>
            </GridItem>
          ))}
        </Grid>
      </div>
    </>
  );
}

export default Recipes_api;
