import React, { useState, useEffect } from "react";
import axios from "axios";
import { Heading, GridItem, Grid, Image, Text, HStack } from "@chakra-ui/react";
function Delicious() {
  const [recipe, setRecipe] = useState([]);

  useEffect(() => {
    const loadPost = async () => {
      const response = await axios.get(
        "http://95.111.202.157:8001/api/v1/getAllRecipes"
      );
      console.log("ajay", response);

      setRecipe(response.data);
    };

    loadPost();
  }, []);

  return (
    <>
      <HStack marginTop="30px">
        <Heading
          fontSize="40px"
          fontWeight="bold"
          marginLeft="20px"
          marginRight="200px"
        >
          try this delicious recipe
          <br /> to make your day
        </Heading>
        <Text align="center" marginTop="100px">
          Lorem ipsum dolor sit amet consectetur ipsum dolor sit amet
          <br />
          consectetur ipsum dolor sit amet consectetur,
        </Text>
      </HStack>
      <Grid templateColumns="repeat(3, 1fr)" gap={6} m={10}>
        {recipe.slice(1, 7).map((image) => (
          <GridItem
            w="100%"
            h="10px"
            key={image._id}
            m={5}
            marginBottom="300px"
            marginLeft="90px"
          >
                <Box  p={5} bg="cyan.400"  borderRadius={20} as="article">
    <Image h="350px" objectFit='fill' w="100%" src={img} alt="stock image"/>
   <Heading size="xl" fontWeight="bold"> Blog Title </Heading> 
   <Text noOfLines={2}> Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse elementum urna quam. Aenean risus turpis, aliquet id diam et, lobortis pellentesque ex. Nulla facilisi. Maecenas. </Text>
    </Box> 
            <Image
              border="2px solid black"
              w="200px"
              height="200px"
              src={"http://95.111.202.157:8001/" + image.recipeId.image}
            />
            <Text width="250px" height="6px">
              title={image.recipeId.title}
              time={image.recipeId.cookTime}
              category={image.recipeId.categoryId.categoryName}
            </Text>
          </GridItem>
        ))}
      </Grid>
    </>
  );
}

export default Delicious;
